package main

import (
	"encoding/hex"
	"github.com/stretchr/testify/assert"
	"math/big"
	"testing"
	"vdf_go"
)

func Test1(t *testing.T) {
	seed := []byte{0xde, 0xad, 0xbe, 0xef}
	D := vdf_go.CreateDiscriminant(seed, 2048)

	X := vdf_go.NewClassGroupFromAbDiscriminant(big.NewInt(2), big.NewInt(1), D)
	y_buf, proof_buf := vdf_go.Create_proof_of_time_wesolowski(D, X, 10, 2048)

	str_y := hex.EncodeToString(y_buf)
	str_proof := hex.EncodeToString((proof_buf))
	assert.Equal(t, str_y, "", "y match")
	assert.Equal(t, str_proof, "", "proof match")
}
